    methods
        function obj = LensShadingGridViewer(varargin)
            %%
            x = size(varargin,2);
            for i = 1:2:x
                obj.(varargin{i}) = varargin{i+1};
            end

            im(:,:,1) = obj.r0
            im(:,:,2) = obj.gr0
            im(:,:,3) = obj.b0

            Max = max(max(max(im)))
            image(im/Max)
        end
    end
